mapper.sql:
SELECT A.N_ID_ACTA, A.C_DESCITEM, A.D_REGISTROACTA, A.C_COMENTACTA, A.C_CODIGOACTA, B.NOMBRE_PARAMETRO AS PARAMENTRO_TIPO_ACTA FROM SIMCO_REG_ACTA_ACUERDO A INNER JOIN SIMCO_PARAMETRO B ON A.C_TIPO_REGISTRO = B.VALOR_PARAMETRO AND B.PADRE_PARAMETRO = 30
A.C_DESCITEM=#{descripcionItem}
A.N_ID_ACTA=#{id}

mapper.select:
    SELECT T2.* FROM
        (
		SELECT  ROWNUM R, T1.* FROM
                (

		) T1


                  ROWNUM  <=  #{numeroRegistroFinal}


	) T2


              T2.R >   #{numeroRegistroInicial}

mapper.update:
UPDATE SIMCO_REG_ACTA_ACUERDO

        C_DESCITEM=UPPER(#{descripcionItem}),
        D_REGISTROACTA=#{fechaRegistro},
        C_COMENTACTA=#{comentario},
        C_TIPO_REGISTRO=#{tipo}


        N_ID_ACTA=#{id}
